    function onSelect(e,index){

        var name = '';
        lsgggl.all.map((j)=>{

            if (e == j.key) {
                name = j.value;

            }
        });

        var isRepeat = false;
        lscpxx.specList.map((i)=>{

            if (i.specId == e) {
                isRepeat = true;

            }

        })

        if (isRepeat) {

            message.warning('添加的'+ name +'规格重复');
            return;
        }

        lscpxx.specList[index].specId = e;
        lscpxx.specList[index].name = name ;

        dispatch({
            type: 'lscpxx/updatePayload', payload: {
                specList: lscpxx.specList
            }
        })


    }

