    //限制小数点后只能输入两位
    function onChangeValueFormatter(value){
        var regex = "^[0-9]*(\.[0-9]{0,2})?$";
        var patt = new RegExp(regex);
        let isPatt = patt.test(value);
        let valueNew = value;

        if(!isPatt && valueNew){

            valueNew = valueNew.substring(0,valueNew.length-1);
        }

        return valueNew;
    }

